   // --- High-DPI Canvas Enhancement ---
   function setupHighDPICanvas(canvas, ctx, scaleFactor = 2) {
       console.log(`üîß [HIGH-DPI] Starting setupHighDPICanvas with scaleFactor: ${scaleFactor}`);

       // Get the device pixel ratio, fallback to 1 if not available
       const dpr = window.devicePixelRatio || 1;
       console.log(`üîß [HIGH-DPI] Device pixel ratio: ${dpr}`);

       // Apply additional scaling factor (2x or 4x for higher quality)
       const totalScale = dpr * scaleFactor;
       console.log(`üîß [HIGH-DPI] Total scale factor: ${totalScale} (dpr: ${dpr} √ó scaleFactor: ${scaleFactor})`);

       // Get the display size (CSS pixels) - use the original canvas size as reference
       const displayWidth = 2048;  // Original canvas width
       const displayHeight = 2048; // Original canvas height

       // Log current canvas state before changes
       console.log(`üîß [HIGH-DPI] BEFORE - Canvas dimensions: ${canvas.width}x${canvas.height}`);
       console.log(`üîß [HIGH-DPI] BEFORE - Canvas style: ${canvas.style.width} x ${canvas.style.height}`);
       console.log(`üîß [HIGH-DPI] BEFORE - Current transform:`, ctx.getTransform());

       // Reset any existing transformations before setting up new ones
       ctx.setTransform(1, 0, 0, 1, 0, 0);
       console.log(`üîß [HIGH-DPI] Reset transform to identity`);

       // Set the actual canvas size in memory (scaled up for higher quality)
       canvas.width = displayWidth * totalScale;
       canvas.height = displayHeight * totalScale;
       console.log(`üîß [HIGH-DPI] Set canvas dimensions to: ${canvas.width}x${canvas.height}`);

       // Scale the canvas back down using CSS to maintain the same visual size
       canvas.style.width = displayWidth + 'px';
       canvas.style.height = displayHeight + 'px';
       console.log(`üîß [HIGH-DPI] Set CSS dimensions to: ${canvas.style.width} x ${canvas.style.height}`);

       // Scale the drawing context so everything draws at the higher resolution
       ctx.scale(totalScale, totalScale);
       console.log(`üîß [HIGH-DPI] Applied context scaling: ${totalScale}x`);
       console.log(`üîß [HIGH-DPI] AFTER - Current transform:`, ctx.getTransform());

       // Enable better text rendering
       ctx.textRenderingOptimization = 'optimizeQuality';
       ctx.imageSmoothingEnabled = true;
       ctx.imageSmoothingQuality = 'high';
       console.log(`üîß [HIGH-DPI] Enabled high-quality rendering options`);

       // Store the scale factor for coordinate calculations
       canvas.scaleFactor = totalScale;
       console.log(`üîß [HIGH-DPI] Stored scaleFactor on canvas: ${canvas.scaleFactor}`);

       console.log(`‚úÖ [HIGH-DPI] Canvas enhanced with ${scaleFactor}x scaling (total: ${totalScale}x)`);
       console.log(`‚úÖ [HIGH-DPI] Final canvas dimensions: ${canvas.width}x${canvas.height} (display: ${displayWidth}x${displayHeight})`);
       return totalScale;
   }

