   // --- Text Wrapping Function ---
   function wrapText(text, maxCharsPerLine) {
       console.log('üì¶ WRAP TEXT: Input text:', text, 'Max chars per line:', maxCharsPerLine);

       // If maxCharsPerLine is 0, return original text (unlimited)
       if (maxCharsPerLine === 0) {
           console.log('üì¶ WRAP TEXT: Unlimited mode, returning original text');
           return text;
       }

       // Split text into words
       const words = text.split(' ');
       const lines = [];
       let currentLine = '';

       for (const word of words) {
           // Check if adding this word would exceed the character limit
           const testLine = currentLine ? `${currentLine} ${word}` : word;

           if (testLine.length <= maxCharsPerLine) {
               // Word fits, add it to current line
               currentLine = testLine;
           } else {
               // Word doesn't fit
               if (currentLine) {
                   // Save current line and start new line with this word
                   lines.push(currentLine);
                   currentLine = word;
               } else {
                   // Single word is longer than limit, add it anyway
                   lines.push(word);
                   currentLine = '';
               }
           }
       }

       // Add the last line if it has content
       if (currentLine) {
           lines.push(currentLine);
       }

       const wrappedText = lines.join('\n');
       console.log('üì¶ WRAP TEXT: Result:', wrappedText);
       return wrappedText;
   }

   // --- Multi-Line Text Rendering Helper ---
   function fillMultiLineText(ctx, text, x, y, obj) {
       console.log('üì¶ ===== FILL MULTI LINE TEXT FLOW START =====');
       console.log('üì¶ MULTI-LINE: *** FUNCTION CALLED *** Rendering text:', text, 'at position:', x, y);
       console.log('üì¶ MULTI-LINE: Object details:', {
           id: obj?.id,
           textAlign: obj?.textAlign,
           textBoxWidth: obj?.textBoxWidth,
           fontSize: obj?.fontSize,
           lineHeight: obj?.lineHeight
       });
       console.log('üöÄ CACHE TEST: fillMultiLineText function loaded with latest changes!');

       // Split text into lines
       const lines = text.split('\n');
       console.log('üì¶ MULTI-LINE: Split into', lines.length, 'lines:', lines);

       if (lines.length === 1) {
           // Single line, use normal fillText
           console.log('üì¶ MULTI-LINE: Single line detected, rendering at x:', x, 'y:', y);
           ctx.fillText(text, x, y);
           console.log('üì¶ MULTI-LINE: Single line rendered');
           console.log('üì¶ ===== FILL MULTI LINE TEXT FLOW END (SINGLE LINE) =====');
           return;
       }

       // Multi-line text
       const fontSize = obj ? obj.fontSize : 20; // Default font size if obj not provided
       const lineHeightMultiplier = obj && obj.lineHeight ? obj.lineHeight : 1.2; // Use object's line height or default
       const lineHeight = fontSize * lineHeightMultiplier;
       const textAlign = obj && obj.textAlign ? obj.textAlign : 'left'; // Use object's text alignment or default
       const textBoxWidth = obj && obj.textBoxWidth ? obj.textBoxWidth : 0; // Get text box width

       console.log('üì¶ MULTI-LINE: Rendering', lines.length, 'lines with line height:', lineHeight, 'alignment:', textAlign);
       console.log('üì¶ MULTI-LINE: textBoxWidth:', textBoxWidth, 'centerX passed:', x);

       // Calculate starting Y position to center the text block
       const totalHeight = (lines.length - 1) * lineHeight;
       const startY = y - totalHeight / 2;

       // Calculate correct X position based on text alignment and text box width
       let renderX = x;
       if (textBoxWidth > 0) {
           // textBoxWidth is a character limit, not pixel width
           // Calculate the pixel width based on the character limit, not the actual text
           // Use dynamic padding that prevents source rectangle from going out of bounds
           // Canvas width is typically 8192, so we need to ensure text box doesn't exceed ~7000 pixels
           const maxSafePadding = Math.max(1, Math.min(10, Math.floor((7000 - textBoxWidth * 135) / 135)));
           const safePadding = Math.max(1, maxSafePadding);
           const sampleText = 'A'.repeat(textBoxWidth + safePadding);
           const actualTextBoxPixelWidth = ctx.measureText(sampleText).width;

           console.log('üì¶ MULTI-LINE RENDER: Character limit:', textBoxWidth, 'Safe padding:', safePadding, 'Text box pixel width:', actualTextBoxPixelWidth);
           // Also calculate the actual text width for comparison/debugging
           let maxLineWidth = 0;
           lines.forEach(line => {
               const lineWidth = ctx.measureText(line).width;
               if (lineWidth > maxLineWidth) {
                   maxLineWidth = lineWidth;
               }
           });

           console.log('üì¶ MULTI-LINE: Character limit:', textBoxWidth, 'Text box pixel width:', actualTextBoxPixelWidth, 'Max line pixel width:', maxLineWidth);

           // CRITICAL FIX: The x parameter is already the correctly positioned renderX from renderStyledObjectToOffscreen
           // Do NOT recalculate textBoxLeft/textBoxRight here - that creates a double offset!
           // Just use the x value directly for the appropriate alignment

           console.log('üì¶ MULTI-LINE: FIXED - Using passed renderX directly (no double calculation):', x);
           renderX = x; // x is already correctly calculated in renderStyledObjectToOffscreen

           if (textAlign === 'left') {
               console.log('üì¶ MULTI-LINE: LEFT alignment - using passed renderX directly:', renderX, '(no double calculation)');
           } else if (textAlign === 'right') {
               console.log('üì¶ MULTI-LINE: RIGHT alignment - using passed renderX directly:', renderX, '(no double calculation)');
           } else if (textAlign === 'center') {
               console.log('üì¶ MULTI-LINE: CENTER alignment - using passed renderX directly:', renderX, '(no double calculation)');
           } else if (textAlign === 'justify') {
               console.log('üì¶ MULTI-LINE: JUSTIFY alignment - using passed renderX directly:', renderX, '(no double calculation)');
           }
       } else {
           console.log('üì¶ MULTI-LINE: No textBoxWidth, using original X:', x);
       }

       // Save current text alignment and set new one
       const originalTextAlign = ctx.textAlign;
       ctx.textAlign = textAlign;

       // Render each line
       console.log('üì¶ MULTI-LINE: About to render', lines.length, 'lines with renderX:', renderX, 'startY:', startY, 'lineHeight:', lineHeight);
       lines.forEach((line, index) => {
           const lineY = startY + (index * lineHeight);
           let lineX = renderX;

           console.log('üì¶ MULTI-LINE: Rendering line', index + 1, 'of', lines.length, ':', line);
           console.log('üì¶ MULTI-LINE: Line position - X:', lineX, 'Y:', lineY);

           // For justify alignment, we might need special handling
           if (textAlign === 'justify' && index < lines.length - 1) {
               // For justify, we'll use left alignment but could add word spacing
               ctx.textAlign = 'left';
               lineX = renderX;
               console.log('üì¶ MULTI-LINE: JUSTIFY mode - using left alignment for line', index + 1);
           }

           console.log('üì¶ MULTI-LINE: Final render position for line', index + 1, '- X:', lineX, 'Y:', lineY, 'ctx.textAlign:', ctx.textAlign);
           ctx.fillText(line, lineX, lineY);
           console.log('üì¶ MULTI-LINE: Line', index + 1, ':', line, 'rendered at X:', lineX, 'Y:', lineY, 'align:', ctx.textAlign);
       });

       // Restore original text alignment
       ctx.textAlign = originalTextAlign;

       console.log('üì¶ MULTI-LINE: All lines rendered successfully');
       console.log('üì¶ ===== FILL MULTI LINE TEXT FLOW END =====');
   }

   // --- SVG Color Recoloring Function ---
   async function recolorSVG(imageObject, newColor) {
       try {
           console.log('üé® [RECOLOR-DEBUG] Starting recolorSVG for:', imageObject.imageUrl, 'to color:', newColor);

           // Fetch the original SVG content
           console.log('üé® [RECOLOR-DEBUG] Fetching SVG content...');
           const response = await fetch(imageObject.imageUrl);
           if (!response.ok) {
               console.error('üé® [RECOLOR-DEBUG] Fetch failed with status:', response.status);
               throw new Error(`Failed to fetch SVG: ${response.status}`);
           }

           console.log('üé® [RECOLOR-DEBUG] Fetch successful, reading text...');
           const svgText = await response.text();
           console.log('üé® [RECOLOR-DEBUG] Original SVG content preview:', svgText.substring(0, 200) + '...');

           // Create a new SVG with the desired color
           console.log('üé® [RECOLOR-DEBUG] Applying SVG color...');
           const coloredSvgText = applySVGColor(svgText, newColor);
           console.log('üé® [RECOLOR-DEBUG] Colored SVG content preview:', coloredSvgText.substring(0, 200) + '...');

           // Create a blob URL for the colored SVG
           console.log('üé® [RECOLOR-DEBUG] Creating blob URL...');
           const blob = new Blob([coloredSvgText], { type: 'image/svg+xml' });
           const coloredUrl = URL.createObjectURL(blob);
           console.log('üé® [RECOLOR-DEBUG] Blob URL created:', coloredUrl);

           // Create a new image with the colored SVG
           console.log('üé® [RECOLOR-DEBUG] Creating new Image element...');
           const img = new Image();
           img.onload = () => {
               console.log('üé® [RECOLOR-DEBUG] Image onload triggered successfully!');
               // Clean up the old blob URL if it exists
               if (imageObject.coloredBlobUrl) {
                   console.log('üé® [RECOLOR-DEBUG] Cleaning up old blob URL:', imageObject.coloredBlobUrl);
                   URL.revokeObjectURL(imageObject.coloredBlobUrl);
               }

               // Update the image object
               console.log('üé® [RECOLOR-DEBUG] Updating image object...');
               imageObject.image = img;
               imageObject.coloredBlobUrl = coloredUrl;
               imageObject.svgColor = newColor;

               // Force a redraw
               console.log('üé® [RECOLOR-DEBUG] Calling update() to redraw canvas...');
               update();

               console.log('üé® [RECOLOR-DEBUG] SVG recolored successfully!');
           };

           img.onerror = (error) => {
               console.error('üé® [RECOLOR-DEBUG] Image onerror triggered!');
               console.error('üé® [RECOLOR-DEBUG] Error loading recolored SVG:', imageObject.imageUrl, error);
               console.error('üé® [RECOLOR-DEBUG] Error event details:', error);
               console.error('üé® [RECOLOR-DEBUG] Colored SVG content preview:', coloredSvgText.substring(0, 500) + '...');
               URL.revokeObjectURL(coloredUrl);

               // Fallback: keep the original image
               console.log('üé® [RECOLOR-DEBUG] Keeping original SVG without color change');
           };

           console.log('üé® [RECOLOR-DEBUG] Setting img.src to blob URL...');
           img.src = coloredUrl;
           console.log('üé® [RECOLOR-DEBUG] img.src set, waiting for onload/onerror...');

       } catch (error) {
           console.error('üé® [RECOLOR-DEBUG] Caught exception in recolorSVG:', error);
           console.error('üé® [RECOLOR-DEBUG] Error stack:', error.stack);
       }
   }

