       console.log(`[SaveTemplate] üé≠ DUPLICATE CHECK COMPLETE: ${canvasObjects.length} -> ${uniqueObjects.length} objects`);

       // Use unique objects for serialization
       const objectsToSerialize = uniqueObjects;

